<div class="flc">	<div id="car_info_content">		<table class="vin_form">			<tr>				<td class="fname"><?= $MSG['VinRequestModule']['msg17'] ?></td>				<td class="fvalue" colspan="3"><?= $car['csc_vin_code'] ?></td>			</tr>			<?			preg_match('~((?P<month>[0-9]){2}[/.,\\-])?(?P<year>[0-9]{2,4})~', $car['csc_date'], $date);			if (empty($car['csc_year'])) $car['csc_year'] = $date['year'];			if (empty($car['csc_month'])) $car['csc_month'] = $date['month'];			?>			<tr>				<td class="fname"><?= $MSG['VinRequestModule']['msg15'] ?></td>				<td class="fvalue"><?= $car['csc_year'] ?></td>				<td class="fname"><?= $MSG['VinRequestModule']['msg16'] ?></td>				<td class="fvalue"><?= $car['csc_month'] ?></td>			</tr>			<tr>				<td class="fname"><?= $MSG['VinRequestModule']['msg13'] ?></td>				<td class="fvalue"><?= $car['csc_brand'] ?></td>				<td class="fname"><?= $MSG['VinRequestModule']['msg14'] ?></td>				<td class="fvalue"><?= $car['csc_model'] ?></td>			</tr>			<tr>				<td class="fname"><?= $MSG['VinRequestModule']['msg32'] ?></td>				<td class="fvalue"><?= $car['csc_engine_power'] ?></td>				<td class="fname"><?= $MSG['VinRequestModule']['msg30'] ?></td>				<td class="fvalue"><?= $car['csc_engine_volume'] ?></td>			</tr>			<tr>				<td class="fname"><?= $MSG['VinRequestModule']['msg40'] ?></td>				<td class="fvalue" colspan="3"><?= $car['csc_more'] ?></td>			</tr>		</table>		<div id="vin_ad">			<a href="#" onclick="return false;" id="a_vin_ad_fields"><span><?= $MSG['Cars']['msg55'] ?></span></a></div>		<div id="vin_ad_fields" style='height:0px;overflow:hidden;'>			<div id="vin_ad_fields_inner">				<table class="vin_form">					<tr>						<td class="fname"><?= $MSG['VinRequestModule']['msg29'] ?></td>						<td class="fvalue"><?= $car['csc_cylinders'] ?></td>						<td class="fname"><?= $MSG['VinRequestModule']['msg31'] ?></td>						<td class="fvalue"><?= $car['csc_valves'] ?></td>					</tr>					<tr>						<td class="fname"><?= $MSG['VinRequestModule']['msg19'] ?></td>						<td class="fvalue"><?=tr($car['csc_body'], 'vin_body_types')?></td>						<td class="fname"><?= $MSG['VinRequestModule']['msg20'] ?></td>						<td class="fvalue"><?= $car['csc_doors'] ?></td>					</tr>					<?					$engineDesc = array();					if ($car['csc_engine_code'] != "") $engineDesc[] = $car['csc_engine_code'];					if ($car['csc_engine_type'] != "") $engineDesc[] = $car['csc_engine_type'];					$car['engine'] = (sizeof($engineDesc) > 0 ? implode(" / ", $engineDesc) : "");					?>					<tr>						<td class="fname"><?= $MSG['VinRequestModule']['msg18'] ?></td>						<td class="fvalue"><?= $car['engine'] ?></td>						<td class="fname"><?= $MSG['VinRequestModule']['msg24'] ?></td>						<td class="fvalue"><?= $car['csc_drive'] ?></td>					</tr>					<tr>						<td class="fname"><?= $MSG['VinRequestModule']['msg37'] ?></td>						<td class="fvalue"><?= $car['csc_transmission_type'] ?></td>						<td class="fname"><?= $MSG['VinRequestModule']['msg38'] ?></td>						<td class="fvalue"><?= $car['csc_transmission_code'] ?></td>					</tr>					<tr>						<td class="fname"><?= $MSG['VinRequestModule']['msg64'] ?></td>						<td class="fvalue">							<? switch ($car['csc_steering_wheel']) {								case 'L':								{									?>									<?= $MSG['VinRequestModule']['msg2'] ?>								<?								}									break;								case 'R':								{									?>									<?= $MSG['VinRequestModule']['msg3'] ?>								<?								}									break;								default:									{									?>									<?= $car['csc_steering_wheel'] ?>									<?									}									break;							} ?>						</td>						<td class="fname"></td>						<td class="fvalue"></td>					</tr>					<tr>						<td class="fname"><?= $MSG['Cars']['msg56'] ?></td>						<td class="fvalue" colspan="3">							<?= (($car['csc_abs'] == 'Y' or $car['csc_abs'] == '1') ? '<span>ABS</span>' : '') ?>							<?= (($car['csc_esp'] == 'Y' or $car['csc_esp'] == '1') ? '<span>ESP</span>' : '') ?>							<?= (($car['csc_booster'] == 'Y' or $car['csc_booster'] == '1') ? '<span>' . $MSG['VinRequestModule']['msg26'] . '</span>' : '') ?>							<?= (($car['csc_conditioner'] == 'Y' or $car['csc_conditioner'] == '1') ? '<span>' . $MSG['VinRequestModule']['msg25'] . '</span>' : '') ?>							<?= (($car['csc_catalyst'] == 'Y' or $car['csc_catalyst'] == '1') ? '<span>' . $MSG['VinRequestModule']['msg27'] . '</span>' : '') ?>						</td>					</tr>				</table>			</div>		</div>		<script type="text/javascript">			window.addEvent('domready', function () {				var myAccordion = new Fx.Accordion($$('#a_vin_ad_fields'), $$('#vin_ad_fields'), {					opacity: false,					display: -1,					alwaysHide: true,					initialDisplayFx: false,					onActive: function (toggler, element) {						setTimeout(function () {							if (toggler.hasClass('active')) element.addClass('vis')						}, 1000);						toggler.addClass('active');					},					onBackground: function (toggler, element) {						toggler.removeClass('active');						if (element.hasClass('vis')) element.removeClass('vis');					}				});			});		</script>	</div></div>